/**
 * System Alert
 *
 * Displays a toast message to everyone in the space.
 *
 * @license MIT
 * @author jjv360
 */
export default class SystemAlertPlugin extends BasePlugin {

    /** Plugin info */
    static id = "jjv360.system-alert"
    static name = "System Alert"
    static description = "Display a message, in a toast, to all users in the space."

    /** Called when the plugin is loaded */
    async onLoad() {

        // Register button
        this.menus.register({
            id: 'jjv360.system-alert',
            text: 'Alert',
            section: 'admin-panel',
            adminOnly: true,
            icon: 'data:image/svg+xml;base64,',
            action: this.onMenuPress.bind(this)
        })

    }

    /** Called when the user presses the System Alert button */
    async onMenuPress() {

        // Ask user for message
        const msg = await this.menus.prompt({
            title: 'Message Everyone',
            text: 'Enter a message. This message will be displayed to everyone in the space currently.'
        })

        // No message to send
        if (!msg) {
            return
        }

        // Send message
        this.messages.send({ action: 'show-msg', text: msg }, true)

    }

    /** Called when a message is received */
    onMessage(msg, fromUserID) {

        // Check message type
        if (msg.action == 'show-msg')
            this.onShowMessage(msg, fromUserID)

    }

    /** Called when we receive a message to display text */
    onShowMessage(msg, fromUserID) {

        // Show toast
        console.debug('[System Alert] Displaying message from ' + fromUserID + ': ' + msg.text)
        this.menus.toast({ text: msg.text, duration: 5000 })

    }

}
